@import "tailwindcss";

@import "tw-animate-css";

:root {
  --background: #f8f9fa;
  --foreground: #0c0c1d;
  --card: #ffffff;
  --card-foreground: #0c0c1d;
  --popover: #ffffff;
  --popover-foreground: #0c0c1d;
  --primary: #ff00c8;
  --primary-foreground: #ffffff;
  --secondary: #f0f0ff;
  --secondary-foreground: #0c0c1d;
  --muted: #f0f0ff;
  --muted-foreground: #0c0c1d;
  --accent: #00ffcc;
  --accent-foreground: #0c0c1d;
  --destructive: #ff3d00;
  --destructive-foreground: #ffffff;
  --border: #dfe6e9;
  --input: #dfe6e9;
  --ring: #ff00c8;
  --chart-1: #ff00c8;
  --chart-2: #9000ff;
  --chart-3: #00e5ff;
  --chart-4: #00ffcc;
  --chart-5: #ffe600;
  --sidebar: #f0f0ff;
  --sidebar-foreground: #0c0c1d;
  --sidebar-primary: #ff00c8;
  --sidebar-primary-foreground: #ffffff;
  --sidebar-accent: #00ffcc;
  --sidebar-accent-foreground: #0c0c1d;
  --sidebar-border: #dfe6e9;
  --sidebar-ring: #ff00c8;
  --font-sans: Outfit, sans-serif;
  --font-serif: ui-serif, Georgia, Cambria, "Times New Roman", Times, serif;
  --font-mono: Fira Code, monospace;
  --radius: 0.5rem;
  --shadow-2xs: 0px 4px 8px -2px hsl(0 0% 0% / 0.05);
  --shadow-xs: 0px 4px 8px -2px hsl(0 0% 0% / 0.05);
  --shadow-sm: 0px 4px 8px -2px hsl(0 0% 0% / 0.1),
    0px 1px 2px -3px hsl(0 0% 0% / 0.1);
  --shadow: 0px 4px 8px -2px hsl(0 0% 0% / 0.1),
    0px 1px 2px -3px hsl(0 0% 0% / 0.1);
  --shadow-md: 0px 4px 8px -2px hsl(0 0% 0% / 0.1),
    0px 2px 4px -3px hsl(0 0% 0% / 0.1);
  --shadow-lg: 0px 4px 8px -2px hsl(0 0% 0% / 0.1),
    0px 4px 6px -3px hsl(0 0% 0% / 0.1);
  --shadow-xl: 0px 4px 8px -2px hsl(0 0% 0% / 0.1),
    0px 8px 10px -3px hsl(0 0% 0% / 0.1);
  --shadow-2xl: 0px 4px 8px -2px hsl(0 0% 0% / 0.25);
  --tracking-normal: 0em;
  --spacing: 0.25rem;
}

.dark {
  --background: #0c0c1d;
  --foreground: #eceff4;
  --card: #1e1e3f;
  --card-foreground: #eceff4;
  --popover: #1e1e3f;
  --popover-foreground: #eceff4;
  --primary: #ff00c8;
  --primary-foreground: #ffffff;
  --secondary: #1e1e3f;
  --secondary-foreground: #eceff4;
  --muted: #1e1e3f;
  --muted-foreground: #8085a6;
  --accent: #00ffcc;
  --accent-foreground: #0c0c1d;
  --destructive: #ff3d00;
  --destructive-foreground: #ffffff;
  --border: #2e2e5e;
  --input: #2e2e5e;
  --ring: #ff00c8;
  --chart-1: #ff00c8;
  --chart-2: #9000ff;
  --chart-3: #00e5ff;
  --chart-4: #00ffcc;
  --chart-5: #ffe600;
  --sidebar: #0c0c1d;
  --sidebar-foreground: #eceff4;
  --sidebar-primary: #ff00c8;
  --sidebar-primary-foreground: #ffffff;
  --sidebar-accent: #00ffcc;
  --sidebar-accent-foreground: #0c0c1d;
  --sidebar-border: #2e2e5e;
  --sidebar-ring: #ff00c8;
  --font-sans: Outfit, sans-serif;
  --font-serif: ui-serif, Georgia, Cambria, "Times New Roman", Times, serif;
  --font-mono: Fira Code, monospace;
  --radius: 0.5rem;
  --shadow-2xs: 0px 4px 8px -2px hsl(0 0% 0% / 0.05);
  --shadow-xs: 0px 4px 8px -2px hsl(0 0% 0% / 0.05);
  --shadow-sm: 0px 4px 8px -2px hsl(0 0% 0% / 0.1),
    0px 1px 2px -3px hsl(0 0% 0% / 0.1);
  --shadow: 0px 4px 8px -2px hsl(0 0% 0% / 0.1),
    0px 1px 2px -3px hsl(0 0% 0% / 0.1);
  --shadow-md: 0px 4px 8px -2px hsl(0 0% 0% / 0.1),
    0px 2px 4px -3px hsl(0 0% 0% / 0.1);
  --shadow-lg: 0px 4px 8px -2px hsl(0 0% 0% / 0.1),
    0px 4px 6px -3px hsl(0 0% 0% / 0.1);
  --shadow-xl: 0px 4px 8px -2px hsl(0 0% 0% / 0.1),
    0px 8px 10px -3px hsl(0 0% 0% / 0.1);
  --shadow-2xl: 0px 4px 8px -2px hsl(0 0% 0% / 0.25);
}

@theme inline {
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-destructive-foreground: var(--destructive-foreground);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);

  --font-sans: var(--font-sans);
  --font-mono: var(--font-mono);
  --font-serif: var(--font-serif);

  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);

  --shadow-2xs: var(--shadow-2xs);
  --shadow-xs: var(--shadow-xs);
  --shadow-sm: var(--shadow-sm);
  --shadow: var(--shadow);
  --shadow-md: var(--shadow-md);
  --shadow-lg: var(--shadow-lg);
  --shadow-xl: var(--shadow-xl);
  --shadow-2xl: var(--shadow-2xl);
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
  }
}
