@import "tailwindcss";

@import "tw-animate-css";

@layer base {
  :root {
    --background: #f8f7fa;
    --foreground: #3d3c4f;
    --card: #ffffff;
    --card-foreground: #3d3c4f;
    --popover: #ffffff;
    --popover-foreground: #3d3c4f;
    --primary: #8a79ab;
    --primary-foreground: #f8f7fa;
    --secondary: #dfd9ec;
    --secondary-foreground: #3d3c4f;
    --muted: #dcd9e3;
    --muted-foreground: #6b6880;
    --accent: #e6a5b8;
    --accent-foreground: #4b2e36;
    --destructive: #d95c5c;
    --destructive-foreground: #f8f7fa;
    --border: #cec9d9;
    --input: #eae7f0;
    --ring: #8a79ab;
    --chart-1: #8a79ab;
    --chart-2: #e6a5b8;
    --chart-3: #77b8a1;
    --chart-4: #f0c88d;
    --chart-5: #a0bbe3;
    --sidebar: #f1eff5;
    --sidebar-foreground: #3d3c4f;
    --sidebar-primary: #8a79ab;
    --sidebar-primary-foreground: #f8f7fa;
    --sidebar-accent: #e6a5b8;
    --sidebar-accent-foreground: #4b2e36;
    --sidebar-border: #d7d2e0;
    --sidebar-ring: #8a79ab;
    --font-sans: Geist, sans-serif;
    --font-serif: "Lora", Georgia, serif;
    --font-mono: "Fira Code", "Courier New", monospace;
    --radius: 0.5rem;
    --shadow-2xs: 1px 2px 5px 1px hsl(0 0% 0% / 0.03);
    --shadow-xs: 1px 2px 5px 1px hsl(0 0% 0% / 0.03);
    --shadow-sm: 1px 2px 5px 1px hsl(0 0% 0% / 0.06),
      1px 1px 2px 0px hsl(0 0% 0% / 0.06);
    --shadow: 1px 2px 5px 1px hsl(0 0% 0% / 0.06),
      1px 1px 2px 0px hsl(0 0% 0% / 0.06);
    --shadow-md: 1px 2px 5px 1px hsl(0 0% 0% / 0.06),
      1px 2px 4px 0px hsl(0 0% 0% / 0.06);
    --shadow-lg: 1px 2px 5px 1px hsl(0 0% 0% / 0.06),
      1px 4px 6px 0px hsl(0 0% 0% / 0.06);
    --shadow-xl: 1px 2px 5px 1px hsl(0 0% 0% / 0.06),
      1px 8px 10px 0px hsl(0 0% 0% / 0.06);
    --shadow-2xl: 1px 2px 5px 1px hsl(0 0% 0% / 0.15);
    --tracking-normal: 0em;
    --spacing: 0.25rem;
  }

  .dark {
    --background: #1a1823;
    --foreground: #e0ddef;
    --card: #232030;
    --card-foreground: #e0ddef;
    --popover: #232030;
    --popover-foreground: #e0ddef;
    --primary: #a995c9;
    --primary-foreground: #1a1823;
    --secondary: #5a5370;
    --secondary-foreground: #e0ddef;
    --muted: #242031;
    --muted-foreground: #a09aad;
    --accent: #372e3f;
    --accent-foreground: #f2b8c6;
    --destructive: #e57373;
    --destructive-foreground: #1a1823;
    --border: #302c40;
    --input: #2a273a;
    --ring: #a995c9;
    --chart-1: #a995c9;
    --chart-2: #f2b8c6;
    --chart-3: #77b8a1;
    --chart-4: #f0c88d;
    --chart-5: #a0bbe3;
    --sidebar: #16141e;
    --sidebar-foreground: #e0ddef;
    --sidebar-primary: #a995c9;
    --sidebar-primary-foreground: #1a1823;
    --sidebar-accent: #372e3f;
    --sidebar-accent-foreground: #f2b8c6;
    --sidebar-border: #2a273a;
    --sidebar-ring: #a995c9;
    --font-sans: Geist, sans-serif;
    --font-serif: "Lora", Georgia, serif;
    --font-mono: "Fira Code", "Courier New", monospace;
    --radius: 0.5rem;
    --shadow-2xs: 1px 2px 5px 1px hsl(0 0% 0% / 0.03);
    --shadow-xs: 1px 2px 5px 1px hsl(0 0% 0% / 0.03);
    --shadow-sm: 1px 2px 5px 1px hsl(0 0% 0% / 0.06),
      1px 1px 2px 0px hsl(0 0% 0% / 0.06);
    --shadow: 1px 2px 5px 1px hsl(0 0% 0% / 0.06),
      1px 1px 2px 0px hsl(0 0% 0% / 0.06);
    --shadow-md: 1px 2px 5px 1px hsl(0 0% 0% / 0.06),
      1px 2px 4px 0px hsl(0 0% 0% / 0.06);
    --shadow-lg: 1px 2px 5px 1px hsl(0 0% 0% / 0.06),
      1px 4px 6px 0px hsl(0 0% 0% / 0.06);
    --shadow-xl: 1px 2px 5px 1px hsl(0 0% 0% / 0.06),
      1px 8px 10px 0px hsl(0 0% 0% / 0.06);
    --shadow-2xl: 1px 2px 5px 1px hsl(0 0% 0% / 0.15);
  }

  @theme inline {
    --color-background: var(--background);
    --color-foreground: var(--foreground);
    --color-card: var(--card);
    --color-card-foreground: var(--card-foreground);
    --color-popover: var(--popover);
    --color-popover-foreground: var(--popover-foreground);
    --color-primary: var(--primary);
    --color-primary-foreground: var(--primary-foreground);
    --color-secondary: var(--secondary);
    --color-secondary-foreground: var(--secondary-foreground);
    --color-muted: var(--muted);
    --color-muted-foreground: var(--muted-foreground);
    --color-accent: var(--accent);
    --color-accent-foreground: var(--accent-foreground);
    --color-destructive: var(--destructive);
    --color-destructive-foreground: var(--destructive-foreground);
    --color-border: var(--border);
    --color-input: var(--input);
    --color-ring: var(--ring);
    --color-chart-1: var(--chart-1);
    --color-chart-2: var(--chart-2);
    --color-chart-3: var(--chart-3);
    --color-chart-4: var(--chart-4);
    --color-chart-5: var(--chart-5);
    --color-sidebar: var(--sidebar);
    --color-sidebar-foreground: var(--sidebar-foreground);
    --color-sidebar-primary: var(--sidebar-primary);
    --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
    --color-sidebar-accent: var(--sidebar-accent);
    --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
    --color-sidebar-border: var(--sidebar-border);
    --color-sidebar-ring: var(--sidebar-ring);

    --font-sans: var(--font-sans);
    --font-mono: var(--font-mono);
    --font-serif: var(--font-serif);

    --radius-sm: calc(var(--radius) - 4px);
    --radius-md: calc(var(--radius) - 2px);
    --radius-lg: var(--radius);
    --radius-xl: calc(var(--radius) + 4px);

    --shadow-2xs: var(--shadow-2xs);
    --shadow-xs: var(--shadow-xs);
    --shadow-sm: var(--shadow-sm);
    --shadow: var(--shadow);
    --shadow-md: var(--shadow-md);
    --shadow-lg: var(--shadow-lg);
    --shadow-xl: var(--shadow-xl);
    --shadow-2xl: var(--shadow-2xl);
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
  }
}
